import React, {Component} from 'react';
import Searchbox from './Searchbox';
import Cardlist from './Cardlist';
import Scrollview from './Scrollview';
import Cardlist2 from './Cardlist2';
import './App.css'
import './swdb.png'

const urls = 
["https://swapi.co/api/people",
  "https://swapi.co/api/people?page=2",
  "https://swapi.co/api/people?page=3",
  "https://swapi.co/api/people?page=4" ]

const urls2 = 
["https://swapi.co/api/planets",
"https://swapi.co/api/planets?page=2",
"https://swapi.co/api/planets?page=3",
"https://swapi.co/api/planets?page=4" ]

//async test exercise **kept it in here for posterity

async function fetchAll() {
  const results = await Promise.all(urls.map((url) => fetch(url).then((r) => r.json())));
  console.log(JSON.stringify(results, null, 2));
    }

class App extends Component {
    constructor() {
      super()
      this.state= {
        planetsList:[],
        peopleList:[],
        searchfield:"",
        isHidden: true,
        isHidden2: true
      }
    }
    toggleHidden () {
      this.setState({
        isHidden: !this.state.isHidden
      })
    }

    toggleHidden2 () {
      this.setState({
        isHidden2: !this.state.isHidden2
      })
    }


fetchData = () => {
  Promise.all(urls.map(url =>
    fetch(url).then(people => people.json())
    .then(people => people.results)
))
  .then(users => this.setState({peopleList:[].concat(...users)} ))
  .catch((error) => {
    console.log(error, "catch the hoop")
  })
}

fetchData2 = () => {
  Promise.all(urls2.map(url =>
    fetch(url).then(planets => planets.json())
    .then(results => results.results)
))
  .then(planets => this.setState({planetsList:[].concat(...planets)} ))
  .catch((error) => {
    console.log(error, "catch the hoop")
  })
}

   componentDidMount() {
    this.fetchData();
    this.fetchData2();
    fetchAll();
}

onSearchChage = (event) => {
  this.setState({searchfield: event.target.value})

  }

render () {
  const filteredPeople = this.state.peopleList.filter(person => {
    return (person.name.toLowerCase().includes(this.state.searchfield.toLowerCase())
    )});
    const filteredPlanets = this.state.planetsList.filter(planet => {
      return (planet.name.toLowerCase().includes(this.state.searchfield.toLowerCase())
      );

  })
  return ( 
    <div class="stars">
<div class="twinkling">
<div class="clouds">
  <div className="tc">
  <h1><img src="swdb.png" /></h1>
  <br></br>
  <Searchbox searchChange={this.onSearchChage} />
  <p></p>
  <br></br>
  <Scrollview>
  <button id="peoplebtn" onClick={this.toggleHidden.bind(this)} >
          People
        </button>

        <button id="planetbtn" onClick={this.toggleHidden2.bind(this)} >
          Planets
        </button>
           {!this.state.isHidden && <Cardlist peopleList={filteredPeople} />} 
           {!this.state.isHidden2 && <Cardlist2 planetsList={filteredPlanets} />}
 </Scrollview>
  </div>
  </div>
  </div>
  </div>
  )
  }
};
export default App
