import React, {Component} from 'react';
import Searchbox from './Searchbox';
import Cardlist from './Cardlist';
import Scrollview from './Scrollview';
import Cardlist2 from './Cardlist2';
import Cardlist3 from './Cardlist3';
import './App.css'
import swdb from './swdb.png'
import { Power2, TimelineLite } from "gsap";
import volumeOff from "./volume_off.svg";
import volumeOn from "./volume_on.svg";


const urls = 
["https://swapi.co/api/people",
  "https://swapi.co/api/people?page=2",
  "https://swapi.co/api/people?page=3",
  "https://swapi.co/api/people?page=4" ]

const urls2 = 
["https://swapi.co/api/planets",
"https://swapi.co/api/planets?page=2",
"https://swapi.co/api/planets?page=3",
"https://swapi.co/api/planets?page=4" ]

const urls3 = 
["https://swapi.co/api/starships",
"https://swapi.co/api/starships?page=2",
"https://swapi.co/api/starships?page=3",
"https://swapi.co/api/starships?page=4" ]

//async test exercise **kept it in here for posterity

async function fetchAll() {
  const results = await Promise.all(urls.map((url) => fetch(url).then((r) => r.json())));
  console.log(JSON.stringify(results, null, 2));
    }

class App extends Component {
    constructor() {
      super()
      this.state= {
        planetsList:[],
        peopleList:[],
        starshipList:[],
        searchfield:"",
        isHidden: true,
        isHidden2: true,
        isHidden3: true,
        muted: true

      }
      this.audio = React.createRef();
      this.intro = React.createRef();

    }

    onVolumeClick = () => {
      if (this.state.muted) {
        this.audio.current.muted = false;
      } else {
        this.audio.current.muted = true;
      }

      this.setState({ muted: !this.state.muted });
    };

    toggleHidden () {
      this.setState({
        isHidden: !this.state.isHidden
      })
    }

    toggleHidden2 () {
      this.setState({
        isHidden2: !this.state.isHidden2
      })
    }

    toggleHidden3 () {
      this.setState({
        isHidden3: !this.state.isHidden3
      })
    }


fetchData = () => {
  Promise.all(urls.map(url =>
    fetch(url).then(people => people.json())
    .then(people => people.results)
))
  .then(users => this.setState({peopleList:[].concat(...users)} ))
  .catch((error) => {
    console.log(error, "catch the hoop")
  })
}

fetchData2 = () => {
  Promise.all(urls2.map(url =>
    fetch(url).then(planets => planets.json())
    .then(results => results.results)
))
  .then(planets => this.setState({planetsList:[].concat(...planets)} ))
  .catch((error) => {
    console.log(error, "catch the hoop")
  })
}

fetchData3 = () => {
  Promise.all(urls3.map(url =>
    fetch(url).then(starships => starships.json())
    .then(results => results.results)
))
  .then(ships => this.setState({starshipList:[].concat(...ships)} ))
  .catch((error) => {
    console.log(error, "catch the hoop")
  })
}

   componentDidMount() {
    this.fetchData();
    this.fetchData2();
    this.fetchData3();
    fetchAll();

    const tl = new TimelineLite();

    tl
      .to(this.intro.current, 4.5, { opacity: 1, delay: 1 })
      .to(this.intro.current, 1.5, {
        opacity: 0,
        onComplete: () => {
          this.audio.current.play();
        }
      })

}

onSearchChage = (event) => {
  this.setState({searchfield: event.target.value})

  }

render () {
  const filteredPeople = this.state.peopleList.filter(person => {
    return (person.name.toLowerCase().includes(this.state.searchfield.toLowerCase())
    )});
    const filteredPlanets = this.state.planetsList.filter(planet => {
      return (planet.name.toLowerCase().includes(this.state.searchfield.toLowerCase())
      );

  })
  const filteredStarships = this.state.starshipList.filter(planet => {
    return (planet.name.toLowerCase().includes(this.state.searchfield.toLowerCase())
    );
  })

  return ( 
    <div class="stars">
<div class="twinkling">
<div class="clouds">
  <div className="tc">
  <h1><img src={swdb}  opacity="0.3" height="100px" /></h1>
  <br></br>
  <section className="intro" ref={this.intro}>
          <p id="here2">
            A long time ago, in a galaxy far,<br /> far away....
          </p>
          </section>

  <Searchbox searchChange={this.onSearchChage} />
  <p id="here"> Please click buttons twice to reset due to novice react coding methods. </p>
  <br></br>
  <button id="peoplebtn" disabled={this.isHidden} onClick={this.toggleHidden.bind(this)} >
          People
        </button>
        <button id="planetbtn" onClick={this.toggleHidden2.bind(this)} >
          Planets
        </button>
        <button id="starshipbtn" onClick={this.toggleHidden3.bind(this)} >
          Starships
        </button>
  <Scrollview
  >
        {!this.state.isHidden3 && <Cardlist3 starshipList={filteredStarships} />}
        {!this.state.isHidden2 && <Cardlist2 planetsList={filteredPlanets} />}
           {!this.state.isHidden && <Cardlist peopleList={filteredPeople} />} 


 </Scrollview>
 <p></p>
 <br></br>
 <audio ref={this.audio} muted>
          <source
            type="audio/mpeg"
            src="https://ia801501.us.archive.org/23/items/StarWars_20180709/Star%20Wars.mp3"
          />
        </audio>
        <button className="volume" type="button" onClick={this.onVolumeClick}>
          {/* Icons created by Agarunov Oktay-Abraham from the Noun Project */}
          {/* https://thenounproject.com/agarunov/ */}
          {this.state.muted ? (
            <img src={volumeOff} alt="Volume is off" />
          ) : (
            <img src={volumeOn} alt="Volume is on" />
          )}
        </button>
  </div>
  </div>
  </div>
  </div>
  )
  }
};
export default App
