import React, {Component} from 'react';
import Searchbox from './Searchbox';
import Cardlist from './Cardlist';
import Scrollview from './Scrollview';
import Cardlist2 from './Cardlist2';
import Cardlist3 from './Cardlist3';
import './App.css'
import swdb from './swdb.png'
import volumeOff from "./volume_off.svg";
import volumeOn from "./volume_on.svg";
import {setSearchField, requestPeople, requestStarships, requestPlanets} from './actions'
import {connect} from 'react-redux'


const mapStateToProps = (state) => {
  return {
      searchField: state.searchPeople.searchField,
      peopleList: state.requestPeople.peopleList,
      planetsList: state.requestPlanets.planetsList,
      starshipsList: state.requestStarships.starshipsList,
      error: state.requestPeople.error,
      isPending: state.requestPeople.isPending
  }
}

const mapDispatchToProps = (dispatch) => {
return {
  onSearchChage: (event) => dispatch(setSearchField(event.target.value)),
  onRequestPeople: () => dispatch(requestPeople()),
  onRequestPlanets: () => dispatch(requestPlanets()),
  onRequestStarships: () => dispatch(requestStarships())
}
}

//async test exercise **kept it in here for posterity**
//async function fetchAll() {
//  const results = await Promise.all(urls.map((url) => fetch(url).then((r) => r.json())));
//  console.log(JSON.stringify(results, null, 2));
//    }

class App extends Component {
    constructor() {
      super()
      this.state= {
        planetsList:[],
        peopleList:[],
        starshipList:[],
        searchfield:"",
        isHidden: true,
        isHidden2: true,
        isHidden3: true,
        muted: true

      }
      this.audio = React.createRef();
      this.intro = React.createRef();

    }

    onVolumeClick = () => {
      if (this.state.muted) {
        this.audio.current.muted = false;
      } else {
        this.audio.current.muted = true;
      }

      this.setState({ muted: !this.state.muted });
    };

    toggleHidden () {
      this.setState({
        isHidden: !this.state.isHidden
      })
    }

    toggleHidden2 () {
      this.setState({
        isHidden2: !this.state.isHidden2
      })
    }

    toggleHidden3 () {
      this.setState({
        isHidden3: !this.state.isHidden3
      })
    }

fetchData2 = () => {
  Promise.all(urls2.map(url =>
    fetch(url).then(planets => planets.json())
    .then(results => results.results)
))
  .then(planets => this.setState({planetsList:[].concat(...planets)} ))
  .catch((error) => {
    console.log(error, "catch the hoop")
  })
}

fetchData3 = () => {
  Promise.all(urls3.map(url =>
    fetch(url).then(starships => starships.json())
    .then(results => results.results)
))
  .then(ships => this.setState({starshipList:[].concat(...ships)} ))
  .catch((error) => {
    console.log(error, "catch the hoop")
  })
}

   componentDidMount() {
    this.fetchData2();
    this.fetchData3();
    this.props.onRequestPeople();
}

render () {
  const {searchField, onSearchChage, isPending, peopleList} = this.props;
  const filteredPeople = peopleList.filter(person => {
    return (person.name.toLowerCase().includes(searchField.toLowerCase())
    )});

  return isPending ?
  <h2>LOADING</h2> :
  ( 
    <div className="stars">
<div className="twinkling">
<div className="clouds">
  <div className="tc">
  <h1><img src={swdb}  opacity="0.3" height="100px" /></h1>
  <br></br>
  <section className="intro" ref={this.intro}>
          <p id="here2">
            A long time ago, in a galaxy far,<br /> far away....
          </p>
          </section>

  <Searchbox searchChange={onSearchChage} />
  <p id="here"> Please click buttons twice to reset due to novice react coding methods. </p>
  <br></br>
  <button id="peoplebtn" disabled={this.isHidden} onClick={this.toggleHidden.bind(this)} >
          People
        </button>
        <button id="planetbtn" onClick={this.toggleHidden2.bind(this)} >
          Planets
        </button>
        <button id="starshipbtn" onClick={this.toggleHidden3.bind(this)} >
          Starships
        </button>
  <Scrollview
  >

           {!this.state.isHidden && <Cardlist peopleList={filteredPeople} />} 


 </Scrollview>
 <audio loop ref={this.audio} muted>
          <source
            type="audio/mpeg"
            src="https://ia801501.us.archive.org/23/items/StarWars_20180709/Star%20Wars.mp3"
          />
        </audio>
        <button className="volume" type="button" onClick={this.onVolumeClick}>
          {/* Icons created by Agarunov Oktay-Abraham from the Noun Project */}
          {/* https://thenounproject.com/agarunov/ */}
          {this.state.muted ? (
            <img src={volumeOff} alt="Volume is off" />
          ) : (
            <img src={volumeOn} alt="Volume is on" />
          )}
        </button>
  </div>
  </div>
  </div>
  </div>
  )
  }
};
export default connect(mapStateToProps, mapDispatchToProps)(App)
