import React, {Component} from 'react';
import Searchbox from './Searchbox';
import Cardlist from './Cardlist';
import Scrollview from './Scrollview';
import Cardlist2 from './Cardlist2';

const urls = ["https://swapi.co/api/people",
              "https://swapi.co/api/people?page=2",
              "https://swapi.co/api/people?page=3",
              "https://swapi.co/api/people?page=4" ]

  const urls2 = ["https://swapi.co/api/planets",
  "https://swapi.co/api/planets?page=2",
  "https://swapi.co/api/planets?page=3",
  "https://swapi.co/api/planets?page=4" ]

async function fetchAll() {
  const results = await Promise.all(urls.map((url) => fetch(url).then((r) => r.json())));
  console.log(JSON.stringify(results, null, 2));
    }

class App extends Component {
    constructor() {
      super()
      this.state= {
        robots:[],
        planetsList:[],
        searchfield:"",
    }
  }


fetchData = () => {
  Promise.all(urls.map(url =>
    fetch(url).then(people => people.json())
    .then(people => people.results)
))
  .then(users => this.setState({robots:[].concat(...users)} ))
  .catch((error) => {
    console.log(error, "catch the hoop")
  })
}

fetchData2 = () => {
  Promise.all(urls2.map(url =>
    fetch(url).then(planets => planets.json())
    .then(results => results.results)
))
  .then(planets => this.setState({planetsList:[].concat(...planets)} ))
  .catch((error) => {
    console.log(error, "catch the hoop")
  })
}

    componentDidMount() {
  this.fetchData2();
  this.fetchData();
  fetchAll();


}

onSearchChage = (event) => {
  this.setState({searchfield: event.target.value})

  }

render () {
  const filteredRobots = this.state.robots.filter(robot => {
    return (robot.name.toLowerCase().includes(this.state.searchfield.toLowerCase())
    )});
    const filteredPlanets = this.state.planetsList.filter(planet => {
      return (planet.name.toLowerCase().includes(this.state.searchfield.toLowerCase())
      );

  })
  return ( 
  <div className="tc">
  <h1>Star Wars</h1>
  <Searchbox searchChange={this.onSearchChage} />
  <p></p>
  <Scrollview>
  <Cardlist2 planetsList={filteredPlanets} />
<Cardlist robots={filteredRobots} />

 </Scrollview>
  </div>
  )
  }
};
export default App
