"api3":{},"api4":{},}

class App extends Component {
    constructor() {
      super()
      this.state= {
        robots:[],
        searchfield:"",
        isHidden: true
      }
    }
    toggleHidden () {
      this.setState({
        isHidden: !this.state.isHidden
      })
    }

fetchData = () => {
  Promise.all([api1,api2,api4,api4]
  .then(function(results) {
  combinedData["api1"] = results[0];
  combinedData["api2"] = results[1];
  combinedData["api2"] = results[2];
  combinedData["api2"] = results[3];  
  }))
  .then(combinedData => this.setState({robots:combinedData} ))
  .catch((error) => {
    console.log(error, "catch the hoop")
  })
  }

    componentDidMount() {

  this.fetchData();

}

onSearchChage = (event) => {
  this.setState({searchfield: event.target.value})

  }

render () {
  const filteredRobots = this.state.robots.filter(robot => {
    return (robot.name.toLowerCase().includes(this.state.searchfield.toLowerCase())
    );
  })
  return ( 
  <div className="tc">
  <h1>Star Wars</h1>
  <Searchbox searchChange={this.onSearchChage} />
  <button onClick={this.toggleHidden.bind(this)} >
          People
        </button>
        {!this.state.isHidden && <Cardlist robots={filteredRobots} />} 
  </div>
  )
  }
};
export default App
